from functools import reduce

### Solução que não funciona por Timeout
class Solution(object):
    def smallestSubarrays(self, nums):
        """
        :type nums: List[int]
        :rtype: List[int]
        """

        def bitwise_or(a, b): return a | b
        
        sizes = []
        n = len(nums)
        for i in range(n):
            _max = reduce(bitwise_or, nums[i:])
            for j in range(i+1, n+1):
                if reduce(bitwise_or, nums[i:j]) == _max:
                    break
            sizes.append(j-i)
        
        return sizes


def smallestSubarraysOptimized(self, nums):
  """
  :type nums: List[int]
  :rtype: List[int]
  """

  n = len(nums)
  pos = [1]*n
  last_seen = [0]*30 #n_of_bits(10^9) ~ ceil(log_2(10^9))
  for i in range(n-1, -1, -1):
    for bit_index in range(30):
      if (nums[i] & (1 << bit_index)):
        last_seen[bit_index] = i
      pos[i] = max(pos[i], last_seen[bit_index] - i + 1)
  return pos

